import { useState,useEffect } from "react"

export const Header = () => {

    const [todo , setTodo] = useState("")

    const onChangeInput=(e)=>{
        setTodo(...todo, e.target.value)
    }


    return (
        <div>

            <header className="header">
                <h1>todos</h1>
                <form onSubmit={onChangeInput}>
                    <input onChange={onChangeInput} className="new-todo" placeholder="What needs to be done?" autoFocus />
                </form>
            </header>
        </div>
    )
}
