import { useState, useEffect } from "react"

export const Header = ({ addLists, lists }) => {

    const [todo, setTodo] = useState("")

    useEffect(() => {
        setTodo("")
    }, [lists])

    const onChangeSub = (e) => {
        setTodo(e.target.value)
    }

    const onSubmit = (e) => {
        e.preventDefault()
        if (!todo) return false
        addLists([...lists, todo])
        console.log(todo)
    }

    return (
        <div>
            <header className="header">
                {todo}
                <h1>todos</h1>
                <form onSubmit={onSubmit}>
                    <input value={todo} onChange={onChangeSub} className="new-todo" placeholder="What needs to be done?" autoFocus />
                </form>
            </header>
        </div>
    )
}
