import { useState } from "react"

export const Header = ({addList,list}) => {

    const [todo , setTodo] = useState("")

    const onSubmit = (e) => {
        e.preventDefault()
        if (!todo)return false
        addList([todo])
        console.log(todo)
    }

    return (
        <div>
            <header className="header">
                {todo}
                <h1>todos</h1>
                <form onSubmit={onSubmit}>
                    <input onChange={e=>setTodo(e.target.value)} className="new-todo" placeholder="What needs to be done?" autoFocus />
                </form>
            </header>
        </div>
    )
}
